% SetPoints.m% Interactively set points and show the resulting cosine transform.% click with mouse to set a point; shift-click to erase;%% Note: you MUST option-click to stop the program.  Otherwise% MATLAB never stops the process!!!figure(1);n=16;m=zeros(n);m2=zeros(4*n);m(n/2+1,n/2+1)=.5;setgrayscale;subplot(2,1,1);imacs(m*255);subplot(2,1,2); imacs(m2);subplot(2,1,1);b=1;while b<3	[x y b]=ginput(1);	x=round(x);	y=round(y);	b	if (x>=1)&(x<=n)&(y>=1)&(y<=n)		if b==1			m(x,y)=1;			m(n+2-x,n+2-y)=1;		elseif b==2  % shift click			m(x,y)=0;			m(n+2-x,n+2-y)=0;% 		elseif b==3  % option click% 			m(x,y)=-1;		end;		imacs(m*255);  subplot(2,1,2);  m2(3*n/2+1:5*n/2,3*n/2+1:5*n/2)=m;  imacs(real(fftshift(ifftn(fftshift(m2)))));  pause(0.1);  subplot(2,1,1);	end;end;