function h=imacs(x,y,m)% function h=imacs(x,y,m);% function h=imacs(m);% Draw an image using cartesian coordinates and autoscaling,% with m(1,1) being in the standard position at lower left.% if x and y arguments are given, they set the scaling of ticks.% We assume a 256-level grayscale, as is set by the function SetGrayscale.% If an output argument h is given, the function returns the handle to the% image object.if nargin==3    if isa(m,'integer')        m=single(m);    end;    m=squeeze(m);    mn=min(min(m));    mx=max(max(m));    if (mx-mn)<eps*max(1,(mx+mn))  % Nothing there: show gray        mx=mx+1;        mn=mn-1;    end;    h=image(x,y,255*(m'-mn)/(mx-mn));    axis xy;    else % no scale arguments, just display the x variable.        if isa(x,'integer')        x=single(x);    end;    x=squeeze(x);    if ndims(x)>3        x=x(:,:,1);  % take only the first plane.    end;%     n=size(x);% %     given a 1D vector, try to convert it to a square image%     if any(n(1:2))==1 && mod(sqrt(n(1)),1)==0%         n(1:2)=sqrt(n(1))*[1 1];  % a 1d vector%         x=reshape(x,n);%     end;    mn=min(min(x));    mx=max(max(x));    if (mx-mn)<eps*max(1,(mx+mn))  % Nothing there: show gray        mx=mx+1;        mn=mn-1;    end;    h=image(255*(x'-mn)/(mx-mn));    axis xy;end;if nargout<1    clear hend;